"use strict";angular.module("DataAccessApp").controller("authController",["$scope","$state","authService",function(n,t,i){n.isLoading=!1;n.loginData={useRefreshTokens:!0,username:"",password:""};n.login=function(){n.$applyAsync(function(){n.isLoading=!0;n.error=""});i.login(n.loginData).success(function(){t.go("app.main.home");n.$applyAsync(function(){n.isLoading=!1})}).error(function(t){n.$applyAsync(function(){n.isLoading=!1});t&&t.error_description&&(n.error=t.error_description)})};n.logout=function(){i.logout().success(function(){t.go("app.auth.login")}).error(function(t){t&&t.error_description&&(n.error=t.error_description)})}}]);
